package com.micdog.webhookhub.service; import javax.crypto.Mac; import javax.crypto.spec.SecretKeySpec; import java.nio.charset.StandardCharsets; public class SignService { public static String hmacSha256Hex(String secret,String msg){ try{ Mac mac=Mac.getInstance("HmacSHA256"); mac.init(new SecretKeySpec(secret.getBytes(StandardCharsets.UTF_8),"HmacSHA256")); byte[] out=mac.doFinal(msg.getBytes(StandardCharsets.UTF_8)); StringBuilder sb=new StringBuilder(out.length*2); for(byte b:out) sb.append(String.format("%02x",b)); return sb.toString(); }catch(Exception e){ return ""; } } }