package com.micdog.webhookhub.service; public class TopicMatch { public static boolean match(String patternCsv,String topic){ if(patternCsv==null||patternCsv.isBlank())return false; for(String p:patternCsv.split(",")){ p=p.trim(); if(p.equals("*"))return true; if(p.endsWith(".*")){ String pre=p.substring(0,p.length()-2); if(topic.startsWith(pre+".")) return true; } else if(p.equals(topic)) return true; } return false; } }